mapper.update:
UPDATE user
SET USER_IMG_PATH = #{userImgPath},
    NIC           = #{nic},
    USER_INTRO    = #{userIntro}
WHERE USER_ID = #{userId}

mapper.select:
SELECT USER_IMG_PATH, NIC, USER_INTRO
FROM user
WHERE USER_ID = #{userId}

mapper.update:
UPDATE user
SET PWD = #{newPwd}
WHERE USER_ID = #{userId}
