mapper.select:
SELECT s.SER_ID, s.SER_THUMN_PATH, s.SER_TTL, s.SER_DESC, s.SER_OPEN_DTM, s.SER_INQR_CNT, s.SER_LIK_CNT, s.SER_REP_CNT, s.SER_POST_CNT, s.CHNL_DSGNT_SER_ODR, s.SER_STUS_CHG_DTM, cd.CD_NM as serStusCdNm, u.NIC as userNic FROM series AS s INNER JOIN channel AS c ON s.CHNL_ID = c.CHNL_ID INNER JOIN user as u ON c.CRT_ID = u.USER_ID INNER JOIN common_code as cd ON s.SER_STUS_CD = cd.CD WHERE c.CHNL_URI = #{chnlUri}
ORDER BY s.CHNL_DSGNT_SER_ODR DESC
ORDER BY s.SER_OPEN_DTM DESC
ORDER BY s.SER_LIK_CNT DESC
LIMIT #{pageSize} OFFSET #{offset}

SELECT s.ser_id, s.ser_thumn_path, s.ser_ttl, s.ser_desc, s.ser_open_dtm, s.ser_inqr_cnt, s.ser_lik_cnt, s.ser_rep_cnt, s.ser_post_cnt, s.chnl_dsgnt_ser_odr, s.recen_pblc_post_id, s.chnl_id, cc.cd_nm as serStusCdNm, s.ser_stus_chg_dtm, c.chnl_uri, u.nic as userNic FROM series AS s INNER JOIN common_code as cc on s.ser_stus_cd = cc.cd INNER JOIN channel as c on s.chnl_id = c.chnl_id INNER JOIN user as u on c.crt_id = u.user_id WHERE ser_id = #{serId};
ORDER BY s.CHNL_DSGNT_SER_ODR DESC
ORDER BY s.SER_OPEN_DTM DESC
ORDER BY s.SER_LIK_CNT DESC
LIMIT #{pageSize} OFFSET #{offset}
