mapper.insert:
INSERT INTO subscription (SUBR_ID, USER_ID, CHNL_ID, SUBR_DTM)
SELECT next_id, #{userId}, #{chnlId}, now()
FROM (SELECT IFNULL(MAX(SUBR_ID), 0) + 1 AS next_id FROM subscription) AS tmp;

mapper.select:
SELECT c.CHNL_IMG_PATH, c.CHNL_TTL, c.CHNL_INTRO, u.USER_IMG_PATH, u.NIC, c.CHNL_URI, c.CHNL_ID
FROM subscription s
         INNER JOIN channel c ON s.CHNL_ID = c.CHNL_ID
         INNER JOIN user u ON c.CRT_ID = u.USER_ID
WHERE s.USER_ID = #{userId}
ORDER BY s.SUBR_DTM DESC;
